mapper.insert:
INSERT INTO product_like (
     product_id, user_id, like_at, delete_flag
)
VALUES (
     #{productId}, #{userId}, now(), 0
)

mapper.select:
SELECT product_id
FROM product_like
WHERE user_id = #{userId}

SELECT *
FROM product_like
WHERE product_id = #{productId} AND user_id = #{userId}
