mapper.insert:
INSERT INTO product_like (
     product_id, user_id, like_at
)
VALUES (
     #{productId}, #{userId}, now()
)

mapper.select:
SELECT product_id
FROM product_like
WHERE user_id = #{userId}

SELECT *
FROM product_like
WHERE product_id = #{productId} AND user_id = #{userId}

mapper.update:
UPDATE product
SET like_count = like_count+1
WHERE product_id = #{productId}

UPDATE product
SET like_count = like_count-1
WHERE product_id = #{productId}
